import React, { Component } from 'react';
import DropDown from './Components/DropDown'
import './App.css';
var other_dog_food_data = {
    "Taste of The Wild" : [
        {
            name: "High Prairie Canine Recipe with Roasted Bison & Roasted Venison",
            protein: 32.0,
            fat: 18.0,
            calorie: 3719,
        },
        {
            name: "Pine Forest Canine Recipe with Venison & Legumes",
            protein: 28.0,
            fat: 15.0,
            calorie: 3600,
        },
        {
            name: "Southwest Canyon Canine Recipe with Wild Boar",
            protein: 29.0,
            fat: 15.0,
            calorie: 3600,
        }
    ],
    "Blue Buffalo" :[
        {
            name: "Life Protection Formula速 Chicken and Brown Rice Recipe (For Puppies)",
            protein: 27.0,
            fat: 16.0,
            calorie: 3686,
        },
        {
            name: "Life Protection Formula速 Fish and Brown Rice Recipe",
            protein: 22.0,
            fat: 14.0,
            calorie: 3619,
        }
    ],
    "Diamond Naturals" : [
        {
            name: "Skin & coat all life stages dog salmon & potato formula",
            protein: 25.0,
            fat: 14.0,
            calorie: 3600,
        },
        {
            name: "Adult Dog beef meal & rice formula",
            protein: 25.0,
            fat: 15.0,
            calorie: 3518,
        }
    ]
}

var nomNomNow_dog_food = [
    {
        name: "Porkalicious Potluck",
        protein: 10.0,
        fat: 4.5,
        calorie: 1275,
    },
    {
        name: "Heartland Beef Mash",
        protein: 8.0,
        fat: 6.0,
        calorie: 1155,
    },
    {
        name: "Chicken Chow-Wow",
        protein: 8.5,
        fat: 6.0,
        calorie: 1189,
    },
    {
        name: "Tasty Turkey Fare",
        protein: 10.0,
        fat: 5.0,
        calorie: 1372,
    }
]
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedDogFood: null,
            selectedRecipe: nomNomNow_dog_food[0]
        };
    }

    selectDogFood(food){
        this.setState({
            selectedDogFood: food
        })
    }
    selectRecipe(recipe){
        this.setState({
            selectedRecipe: recipe
        })
    }

    render() {
        return (
          <div className="App">
              <div className="DogFoodCalculator">
                  <div className="left-side">
                      <h1>Dog food comparison calculator</h1>
                      <div>
                          <img src="https://www.nomnomnow.com/images/home/justin_square.jpg" alt=""/>
                          <p>Dr. Shmalberg says: We should feed by calorie, not by weight. Percentages on commercial dog food labels are not reliable because they are based on weight, and the values are affected by many factors, including moisture, fiber and minerals.</p>
                          <p>Use this calculator to get a more accurate picture of how another dog food brand compares with NomNomNow.
                          </p>
                          <DropDown list={other_dog_food_data}
                                    selected={this.state.selectedDogFood}
                                    hasSubCategory={true}
                                    select={this.selectDogFood.bind(this)}/>

                          <DropDown list={nomNomNow_dog_food}
                                    selected={this.state.selectedRecipe}
                                    select={this.selectRecipe.bind(this)}/>
                      </div>
                  </div>
                  <div className="right-side" >
                      <img className="dog-food" src="https://www.nomnomnow.com//images/home/chicken_bowl_transparent.png" alt="Yummy NomNomNow Dog Food"/>
                  </div>
              </div>
          </div>
        );
    }
}

export default App;
