import React, { Component } from 'react';
import FormField from '../Elements/FormField'
import DropDownList from '../Elements/DropDownList'
class DropDown extends Component {
    constructor(props) {
        super(props);
        this.state = {
            open: false
        };
    }

    openDropDown(){
        console.log("openDropDown")
        this.setState({
            open: !this.state.open
        })
    }

    render() {
        return (
            <div className="Dropdown-container">
                <FormField placeholder="Select a dog food" select={this.openDropDown.bind(this)}/>
                {this.state.open? <DropDownList list={this.props.list} showCategory={this.props.showCategory}/> : null}
            </div>
        );
    }
}
